      <form>
        <input name="title" placeholder="Title" />
        <textarea name="content" placeholder="Take a note..." rows="3" />
        <button>Add</button>
      </form>
    </div>
 */

    const [entryNote, setEntryNote] = useState({
      title: "",
      note: "",
    });

function App() {
  const noteElement = notes.map((note) => (

    <Note 
      key={note.key} 
      title={note.title} 
      content={note.content} 
    />
  ));
  return (
    <div>
      <Header />
      <CreateArea />
      {noteElement}
      <Footer />
    </div>
  );
}

export default App;

