import React, { useState } from "react";

function CreateArea(props) {

  return (
    <div>
      <form>
        <input name="title" placeholder="Title"  value={props.entryNote} />
        <textarea name="content" placeholder="Take a note..." value={props.entryNote} rows="3" />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;

