    //   return prevNotes.filter((notes, index) => {
    //     return index !== id;
    //   });
    // });
    console.log("delete");
    log;

  }

  const noteElement = notes.map((note) => (
    <Note key={note.key} title={note.title} content={note.content} onDelete={deleteNote} />
  ));

  return (
    <div>
      <Header />
      <CreateArea onAdd={addNote} />
      {noteElement}
      <Footer />
    </div>
  );
}

export default App;

